import React, { useState } from 'react';
import './NuevoRegistro.css';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const NuevoRegistro = () => {
  const [formData, setFormData] = useState({
    correo_usuario: '',
    categoria_base: '',
    subcategoria_1: '',
    subcategoria_2: '',
    subcategoria_3: '',
    tipo_aire: '',
    refrigerante: '',
    frecuencia_uso: '',
    horas_dia: '',
    dias_mes: '',
    potencia_w: '',
    voltaje_v: '',
    corriente_a: '',
    observaciones: ''
  });

  const [nuevoAmbiente, setNuevoAmbiente] = useState({
    sede: '',
    bloque: '',
    piso: '',
    tipo_ambiente: '',
    nombre: ''
  });

  const [errores, setErrores] = useState({});
  const [mensaje, setMensaje] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleNuevoAmbienteChange = (e) => {
    setNuevoAmbiente(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }));
  };

  const handleSubmit = async (e, shouldContinue = false) => {
    e.preventDefault();
    setErrores({});
    setMensaje('');
    setLoading(true);

    try {
      // Payload corregido: usamos 'sede' en lugar de 'sede_nombre'
      const ambientePayload = {
        sede: nuevoAmbiente.sede,
        bloque: nuevoAmbiente.bloque,
        piso: nuevoAmbiente.piso,
        tipo_ambiente: nuevoAmbiente.tipo_ambiente,
        nombre: nuevoAmbiente.nombre
      };

      // 1. Crear ambiente
      const ambienteRes = await api.post('/ambientes/', ambientePayload);
      const ambienteId = ambienteRes.data.id;

      // 2. Crear registro con el ID del ambiente
      const registroData = { 
        ...formData, 
        ambiente: ambienteId 
      };
      await api.post('/registros/', registroData);

      setMensaje('✅ Registro creado exitosamente');
      
      if (shouldContinue) {
        // Mantener datos del responsable y ambiente
        setFormData(prev => ({
          correo_usuario: prev.correo_usuario, // Mantener correo
          categoria_base: '',
          subcategoria_1: '',
          subcategoria_2: '',
          subcategoria_3: '',
          tipo_aire: '',
          refrigerante: '',
          frecuencia_uso: '',
          horas_dia: '',
          dias_mes: '',
          potencia_w: '',
          voltaje_v: '',
          corriente_a: '',
          observaciones: ''
        }));
      } else {
        setTimeout(() => navigate('/'), 2000);
      }
    } catch (error) {
      if (error.response?.data) {
        console.log('Errores del servidor:', error.response.data);
        setErrores(error.response.data);
        setMensaje('❌ Error al crear registro');
      } else {
        console.error(error);
        setMensaje('❌ Error inesperado al registrar');
      }
    } finally {
      setLoading(false);
    }
  };

  const mostrarCamposAire = formData.categoria_base.toLowerCase().includes('aire acondicionado');

  return (
    <div className="registro-form">
      <h2>Nuevo Registro Energético</h2>

      {mensaje && <div className={`alert ${mensaje.includes('✅') ? 'success' : 'error'}`}>{mensaje}</div>}

      <form onSubmit={(e) => handleSubmit(e, false)}>
        {/* Sección: Datos del Responsable */}
        <div className="form-section">
          <h3>Datos del Responsable</h3>
          <label>
            Correo del Responsable:
            <input 
              type="email" 
              name="correo_usuario" 
              value={formData.correo_usuario} 
              onChange={handleChange} 
              required 
            />
          </label>
          {errores.correo_usuario && <p className="error">{errores.correo_usuario}</p>}
        </div>

        {/* Sección: Datos del Ambiente */}
        <div className="form-section">
          <h3>Datos del Ambiente</h3>
          <label>
            Sede*:
            <input 
              type="text" 
              name="sede" 
              value={nuevoAmbiente.sede} 
              onChange={handleNuevoAmbienteChange} 
              required 
              minLength="3"
            />
          </label>
          {errores.sede && <p className="error">{errores.sede}</p>}

          <div className="form-row">
            <label>
              Bloque*:
              <input 
                type="text" 
                name="bloque" 
                value={nuevoAmbiente.bloque} 
                onChange={handleNuevoAmbienteChange} 
                required 
              />
            </label>

            <label>
              Piso*:
              <input 
                type="text" 
                name="piso" 
                value={nuevoAmbiente.piso} 
                onChange={handleNuevoAmbienteChange} 
                required 
              />
            </label>
          </div>

          <div className="form-row">
            <label>
              Tipo de Ambiente*:
              <input 
                type="text" 
                name="tipo_ambiente" 
                value={nuevoAmbiente.tipo_ambiente} 
                onChange={handleNuevoAmbienteChange} 
                required 
              />
            </label>

            <label>
              Nombre*:
              <input 
                type="text" 
                name="nombre" 
                value={nuevoAmbiente.nombre} 
                onChange={handleNuevoAmbienteChange} 
                required 
              />
            </label>
          </div>
        </div>

        {/* Sección: Datos del Equipo */}
        <div className="form-section">
          <h3>Datos del Equipo</h3>
          <label>
            Categoría Base*:
            <input 
              type="text" 
              name="categoria_base" 
              value={formData.categoria_base} 
              onChange={handleChange} 
              required 
            />
          </label>

          {mostrarCamposAire && (
            <div className="form-row">
              <label>
                Tipo de Aire*:
                <select 
                  name="tipo_aire" 
                  value={formData.tipo_aire} 
                  onChange={handleChange}
                  required
                >
                  <option value="">Seleccione...</option>
                  <option value="SPLIT">Split</option>
                  <option value="VENTANA">De Ventana</option>
                  <option value="PORTATIL">Portátil</option>
                  <option value="CENTRAL">Central</option>
                </select>
              </label>

              <label>
                Refrigerante*:
                <select 
                  name="refrigerante" 
                  value={formData.refrigerante} 
                  onChange={handleChange}
                  required
                >
                  <option value="">Seleccione...</option>
                  <option value="R22">R-22</option>
                  <option value="R410A">R-410A</option>
                  <option value="R32">R-32</option>
                  <option value="R134A">R-134A</option>
                </select>
              </label>
            </div>
          )}

          <div className="form-row">
            <label>
              Subcategoría 1*:
              <input 
                type="text" 
                name="subcategoria_1" 
                value={formData.subcategoria_1} 
                onChange={handleChange} 
                required 
              />
            </label>

            <label>
              Subcategoría 2:
              <input 
                type="text" 
                name="subcategoria_2" 
                value={formData.subcategoria_2} 
                onChange={handleChange} 
              />
            </label>

            <label>
              Subcategoría 3:
              <input 
                type="text" 
                name="subcategoria_3" 
                value={formData.subcategoria_3} 
                onChange={handleChange} 
              />
            </label>
          </div>
        </div>

        {/* Sección: Datos de Consumo */}
        <div className="form-section">
          <h3>Datos de Consumo</h3>
          <div className="form-row">
            <label>
              Frecuencia de Uso*:
              <input 
                type="text" 
                name="frecuencia_uso" 
                value={formData.frecuencia_uso} 
                onChange={handleChange} 
                required 
              />
            </label>

            <label>
              Horas/Día*:
              <input 
                type="number" 
                name="horas_dia" 
                min="0" 
                max="24" 
                value={formData.horas_dia} 
                onChange={handleChange} 
                required 
              />
            </label>

            <label>
              Días/Mes*:
              <input 
                type="number" 
                name="dias_mes" 
                min="0" 
                max="31" 
                value={formData.dias_mes} 
                onChange={handleChange} 
                required 
              />
            </label>
          </div>

          <div className="form-row">
            <label>
              Potencia (W)*:
              <input 
                type="number" 
                name="potencia_w" 
                min="0" 
                step="0.01" 
                value={formData.potencia_w} 
                onChange={handleChange} 
                required 
              />
            </label>

            <label>
              Voltaje (V):
              <input 
                type="number" 
                name="voltaje_v" 
                step="0.1" 
                value={formData.voltaje_v} 
                onChange={handleChange} 
              />
            </label>

            <label>
              Corriente (A):
              <input 
                type="number" 
                name="corriente_a" 
                step="0.01" 
                value={formData.corriente_a} 
                onChange={handleChange} 
              />
            </label>
          </div>

          <label>
            Observaciones:
            <textarea 
              name="observaciones" 
              value={formData.observaciones} 
              onChange={handleChange} 
              rows="3" 
            />
          </label>
        </div>

        <div className="form-actions">
          <button 
            type="submit" 
            className="btn blue"
            disabled={loading}
          >
            {loading ? 'Guardando...' : 'Guardar Registro'}
          </button>
          
          <button
            type="button"
            className="btn green"
            onClick={(e) => handleSubmit(e, true)}
            disabled={loading}
          >
            {loading ? 'Guardando...' : 'Guardar y Continuar'}
          </button>
          
          <button
            type="button"
            className="btn gray"
            onClick={() => navigate('/registros')}
            disabled={loading}
          >
            Cancelar
          </button>
        </div>
      </form>
    </div>
  );
};

export default NuevoRegistro;
