import React, { useState } from 'react';
import './NuevoRegistro.css';
import { useNavigate } from 'react-router-dom';
import useRegistros from '../hooks/useRegistros';

const NuevoRegistro = () => {
  const navigate = useNavigate();
  const { createRegistro, loading, error } = useRegistros();
  
  // Estado para datos del formulario
  const [formData, setFormData] = useState({
    correo_usuario: '',
    categoria_base: '',
    subcategoria_1: '',
    subcategoria_2: '',
    subcategoria_3: '',
    tipo_aire: '',
    refrigerante: '',
    frecuencia_uso: '',
    horas_dia: '',
    dias_mes: '',
    potencia_w: '',
    voltaje_v: '',
    corriente_a: '',
    observaciones: ''
  });

  // Estado para datos del ambiente (persistente)
  const [ambienteData, setAmbienteData] = useState({
    sede: '',
    bloque: '',
    piso: '',
    tipo_ambiente: '',
    nombre: ''
  });

  const [errores, setErrores] = useState({});
  const [mensaje, setMensaje] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleAmbienteChange = (e) => {
    const { name, value } = e.target;
    setAmbienteData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e, shouldContinue = false) => {
    e.preventDefault();
    setErrores({});
    setMensaje('');

    try {
      await createRegistro({
        ...formData,
        ambiente: ambienteData
      });

      setMensaje('✅ Registro creado exitosamente');
      
      if (shouldContinue) {
        // Mantener datos del responsable y ambiente
        setFormData(prev => ({
          ...prev,
          // Limpiar solo los campos del equipo
          categoria_base: '',
          subcategoria_1: '',
          subcategoria_2: '',
          subcategoria_3: '',
          tipo_aire: '',
          refrigerante: '',
          frecuencia_uso: '',
          horas_dia: '',
          dias_mes: '',
          potencia_w: '',
          voltaje_v: '',
          corriente_a: '',
          observaciones: ''
        }));
      } else {
        setTimeout(() => navigate('/registros'), 2000);
      }
    } catch (err) {
      setMensaje('❌ Error al crear registro');
      if (err.response?.data) {
        setErrores(err.response.data);
      }
    }
  };

  const mostrarCamposAire = formData.categoria_base.toLowerCase().includes('aire acondicionado');

  return (
    <div className="registro-form">
      <h2>Nuevo Registro Energético</h2>

      {mensaje && <div className={`alert ${mensaje.includes('✅') ? 'success' : 'error'}`}>{mensaje}</div>}

      <form onSubmit={(e) => handleSubmit(e, false)}>
        {/* Sección: Datos del Responsable */}
        <div className="form-section">
          <h3>Datos del Responsable</h3>
          <label>
            Correo del Responsable:
            <input 
              type="email" 
              name="correo_usuario" 
              value={formData.correo_usuario} 
              onChange={handleChange} 
              required 
            />
          </label>
          {errores.correo_usuario && <p className="error">{errores.correo_usuario}</p>}
        </div>

        {/* Sección: Datos del Ambiente */}
        <div className="form-section">
          <h3>Datos del Ambiente</h3>
          <label>
            Sede*:
            <input 
              type="text" 
              name="sede" 
              value={ambienteData.sede} 
              onChange={handleAmbienteChange} 
              required 
              minLength="3"
            />
          </label>
          {errores.sede && <p className="error">{errores.sede}</p>}

          <div className="form-row">
            <label>
              Bloque*:
              <input 
                type="text" 
                name="bloque" 
                value={ambienteData.bloque} 
                onChange={handleAmbienteChange} 
                required 
              />
            </label>

            <label>
              Piso*:
              <input 
                type="text" 
                name="piso" 
                value={ambienteData.piso} 
                onChange={handleAmbienteChange} 
                required 
              />
            </label>
          </div>

          <div className="form-row">
            <label>
              Tipo de Ambiente*:
              <input 
                type="text" 
                name="tipo_ambiente" 
                value={ambienteData.tipo_ambiente} 
                onChange={handleAmbienteChange} 
                required 
              />
            </label>

            <label>
              Nombre*:
              <input 
                type="text" 
                name="nombre" 
                value={ambienteData.nombre} 
                onChange={handleAmbienteChange} 
                required 
              />
            </label>
          </div>
        </div>

        {/* Resto de tus secciones del formulario... */}

        <div className="form-actions">
          <button 
            type="submit" 
            className="btn blue"
            disabled={loading}
          >
            {loading ? 'Guardando...' : 'Guardar Registro'}
          </button>
          
          <button
            type="button"
            className="btn green"
            onClick={(e) => handleSubmit(e, true)}
            disabled={loading}
          >
            {loading ? 'Guardando...' : 'Guardar y Continuar'}
          </button>
          
          <button
            type="button"
            className="btn gray"
            onClick={() => navigate('/registros')}
            disabled={loading}
          >
            Cancelar
          </button>
        </div>
      </form>
    </div>
  );
};

export default NuevoRegistro;
